fun main(args: Array<String>) {
    val matrix = Array(3, { IntArray(3) })
    matrix[0][0] = 1
    matrix[0][1] = 2
    matrix[0][2] = 3
    matrix[1][0] = 4
    matrix[1][1] = 5
    matrix[1][2] = 6
    matrix[2][0] = 7
    matrix[2][1] = 8
    matrix[2][2] = 9

    val inpString = "\n" +
            "\n" +
            "\n" +
            "\n" +
            ""

//    val inpString = "ULL\nRRDDD\nLURDL\nUUUUD"

    val inp: List<String> = inpString.split("\n")
    var curr = Pair(1, 1)
    var ans = ""

    for (s in inp) {
        val charArray = s.toCharArray()
        for (char in charArray) {
            if (char == 'L' && curr.second > 0) {
                curr = Pair(curr.first, curr.second - 1)
            } else if (char == 'R' && curr.second < 2) {
                curr = Pair(curr.first, curr.second + 1)
            } else if (char == 'U' && curr.first > 0) {
                curr = Pair(curr.first - 1, curr.second)
            } else if (char == 'D' && curr.first < 2) {
                curr = Pair(curr.first + 1, curr.second)
            }
        }
        ans += matrix[curr.first][curr.second].toString()
    }

    println(curr)
    println("$ans")
}